mapper.update:
UPDATE setmeal
name = #{name}, price = #{price}, description = #{description}, image = #{image}, category_id = #{categoryId}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}

mapper.select:
SELECT s.*, c.name AS categoryName FROM setmeal s LEFT JOIN category c ON s.category_id = c.id


        AND s.name LIKE concat('%', #{name}, '%')


        AND s.category_id = #{categoryId}


        AND s.status = #{status}


ORDER BY s.create_time DESC
